import{_ as e,c as l,o as i,ag as r}from"./chunks/framework.DPDPlp3K.js";const u=JSON.parse('{"title":"2.3 单页面应用","description":"","frontmatter":{},"headers":[],"relativePath":"performanceGeek/2.3单页面应用.md","filePath":"performanceGeek/2.3单页面应用.md"}'),t={name:"performanceGeek/2.3单页面应用.md"};function o(n,a,h,c,s,_){return i(),l("div",null,a[0]||(a[0]=[r('<h1 id="_2-3-单页面应用" tabindex="-1">2.3 单页面应用 <a class="header-anchor" href="#_2-3-单页面应用" aria-label="Permalink to &quot;2.3 单页面应用&quot;">​</a></h1><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h2><p>单页应用(single-page application，缩写SPA)通过动态重写当前页面，来与用户交互，而非传统的从服务器重新加载整个新页面。这种方法在使用过程中不需要重新加载页面，避免了页面之间切换打断用户体验，使应用程序更像一个桌面应用程序。</p><h2 id="技术优点" tabindex="-1">技术优点 <a class="header-anchor" href="#技术优点" aria-label="Permalink to &quot;技术优点&quot;">​</a></h2><ul><li>不错的加载速度:用户往往感觉页面加载非常快，因为一进入页面就能看到页面元素</li><li>良好的交互体验:进行局部渲染，避免不必要的页面间跳转和重复渲染</li><li>前后端职责分离:前端进行页面交互逻辑，后端负责业务逻辑</li><li>减轻服务器负载:服务器只处理数据接口输出，不用考虑页面模板渲染和HTML 展示</li></ul><h2 id="技术缺点" tabindex="-1">技术缺点 <a class="header-anchor" href="#技术缺点" aria-label="Permalink to &quot;技术缺点&quot;">​</a></h2><ul><li>开发成本相对较高</li><li>首次页面加载时间过多</li><li>SEO难度比较大</li></ul><h2 id="技术实现" tabindex="-1">技术实现 <a class="header-anchor" href="#技术实现" aria-label="Permalink to &quot;技术实现&quot;">​</a></h2><ul><li>使用Angular 、React、Vue框架</li></ul><h2 id="选型建议" tabindex="-1">选型建议 <a class="header-anchor" href="#选型建议" aria-label="Permalink to &quot;选型建议&quot;">​</a></h2><ul><li>重交互页面业务</li><li>核心链路场景业务</li></ul>',11)]))}const p=e(t,[["render",o]]);export{u as __pageData,p as default};
